package com.springboot.cqrs.repositories;import com.springboot.cqrs.entities.Category;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;public interface CategoryRepository extends JpaRepository<Category, Long>{    @Modifying    @Query(value = "update Category category set category.createdAt = CURRENT_DATE where category.id = :id")    void deactivate(@Param("id") Long id);    List<Category> findByActiveTrue();    Page<Category> findByActiveTrue(Pageable pageable);}