package com.springboot.cqrs.write.controllers;import com.springboot.cqrs.write.dto.CategoryWriteDTO;import com.springboot.cqrs.write.services.CategoryWriteService;import lombok.extern.slf4j.Slf4j;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@Slf4j@RestController@RequestMapping(value = "/category/write")public class CategoryWriteController {    private CategoryWriteService categoryWriteService;    public CategoryWriteController(CategoryWriteService categoryWriteService) {        this.categoryWriteService = categoryWriteService;    }    @PostMapping    public ResponseEntity<Void> store(CategoryWriteDTO category)    {        categoryWriteService.store(category);        return new ResponseEntity<>(HttpStatus.CREATED);    }    @PutMapping    public ResponseEntity<Void> update(CategoryWriteDTO category)    {        categoryWriteService.update(category);        return new ResponseEntity<>(HttpStatus.OK);    }    @PutMapping(value = "/{id}")    public ResponseEntity<Void> deactivate(@PathVariable Long id)    {        categoryWriteService.deactivate(id);        return new ResponseEntity<>(HttpStatus.OK);    }    @DeleteMapping(value = "/{id}")    public ResponseEntity<Void> delete(@PathVariable Long id)    {        categoryWriteService.delete(id);        return new ResponseEntity<>(HttpStatus.NO_CONTENT);    }}