package com.springboot.cqrs.write.dto;import lombok.*;import java.io.Serializable;@Getter@Setterpublic class CategoryWriteDTO implements Serializable {    private Long id;    private @NonNull String name;    private String description;    @Override    public String toString()    {        return "CategoryWriteDTO{" +            "id=" + id +            ", name='" + name + '\'' +            ", description='" + description + '\'' +            '}';    }}