package com.springboot.cqrs.write.services.impls;import com.springboot.cqrs.write.dto.CategoryWriteDTO;import com.springboot.cqrs.write.services.CategoryWriteService;import com.springboot.cqrs.entities.Category;import com.springboot.cqrs.repositories.CategoryRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Service@Transactionalpublic class CategoryWriteServiceImpl implements CategoryWriteService {    private CategoryRepository categoryRepository;    public CategoryWriteServiceImpl(CategoryRepository categoryRepository) {        this.categoryRepository = categoryRepository;    }    @Override    public void store(CategoryWriteDTO category)    {        Category categoryEntity = new Category(category.getName(), category.getDescription());        categoryRepository.save(categoryEntity);    }    @Override    public void update(CategoryWriteDTO category)    {        Category categoryEntity = new Category(category.getId(), category.getName(), category.getDescription());        categoryRepository.save(categoryEntity);    }    @Override    public void deactivate(Long id) {        categoryRepository.deactivate(id);    }    @Override    public void delete(Long id) {        categoryRepository.deleteById(id);    }}